INSERT INTO persons (id, firstName, lastName, aboutPerson, featured, createdBy, created_at, updated_at, category, visibility, fullName, externalSource, scheduledPublishTime, view_count) VALUES ('159', 'Bruce ', 'Lee', 'Bruce Lee[b] (born Lee Jun-fan;[c] November 27, 1940 – July 20, 1973) was a Hong Kong-American martial artist and actor. He was the founder of Jeet Kune Do, a hybrid martial arts philosophy drawing from different combat disciplines that is sometimes credited with paving the way for the combat sport mixed martial arts (MMA).[3] Lee is considered by some commentators and martial artists to be the most influential martial artist of all time and a pop culture icon of the 20th century, who bridged the gap between East and West. He is credited with promoting Hong Kong action cinema and helping to change the way Chinese people were presented in American films.[4]', 'https://bpikd-backend.up.railway.app/featured/person-159.jpeg', 'admin', 'Sun Jun 02 2024 17:12:53 GMT+0200 (Central European Summer Time)', 'Wed Jun 05 2024 01:07:08 GMT+0200 (Central European Summer Time)', 'Person of Interest', 'Public', 'Bruce  Lee', 'null', 'Sun Jun 02 2024 17:12:53 GMT+0200 (Central European Summer Time)', '159');
