INSERT INTO persons (id, firstName, lastName, aboutPerson, featured, createdBy, created_at, updated_at, category, visibility, fullName, externalSource, scheduledPublishTime, view_count) VALUES ('161', 'Jim', 'Carrey', 'James Eugene Carrey (/ˈkæri/; born January 17, 1962)[2] is a Canadian-American actor and comedian known for his energetic slapstick performances.[3] After spending the 1980s honing his stand-up comedy act and portraying mostly supporting roles in films, Carrey gained wide recognition in 1990 when he was cast in the American sketch comedy television series In Living Color (1990–1994). He broke out as a film star after starring in a string of box office hits with Ace Ventura: Pet Detective, The Mask, and Dumb and Dumber (all 1994), which he followed up with Ace Ventura: When Nature Calls and Batman Forever (both 1995). The success of these five films led to Carrey being the first actor to receive a $20 million salary for performing in films, beginning with The Cable Guy (1996).[4]', 'https://bpikd-backend.up.railway.app/featured/person-161.jpeg', 'admin', 'Sun Jun 02 2024 17:16:32 GMT+0200 (Central European Summer Time)', 'Tue Jun 04 2024 18:49:15 GMT+0200 (Central European Summer Time)', 'Person of Interest', 'Public', 'Jim Carrey', 'null', 'Sun Jun 02 2024 17:16:32 GMT+0200 (Central European Summer Time)', '22');
INSERT INTO persons (id, firstName, lastName, aboutPerson, featured, createdBy, created_at, updated_at, category, visibility, fullName, externalSource, scheduledPublishTime, view_count) VALUES ('162', 'Michael', 'Jordan', 'Michael Jeffrey Jordan (born February 17, 1963), also known by his initials MJ,[9] is an American businessman and former professional basketball player. He played fifteen seasons in the National Basketball Association (NBA) between 1984 and 2003, winning six NBA championships with the Chicago Bulls. He was integral in popularizing basketball and the NBA around the world in the 1980s and 1990s,[10] becoming a global cultural icon.[11] His profile on the NBA website states, "By acclamation, Michael Jordan is the greatest basketball player of all time."[12]', 'https://bpikd-backend.up.railway.app/featured/person-162.jpeg', 'admin', 'Sun Jun 02 2024 17:17:37 GMT+0200 (Central European Summer Time)', 'Tue Jun 04 2024 20:48:23 GMT+0200 (Central European Summer Time)', 'Person of Interest', 'Public', 'Michael Jordan', 'null', 'Sun Jun 02 2024 17:17:37 GMT+0200 (Central European Summer Time)', '37');
