INSERT INTO works (id, person_id, title, content, publishTime, isPublished, scheduledPublishTime, externalSource, visibility, category, createdBy, created_at, updated_at, work_view_count) VALUES ('231', '159', '1959–1964: Continuous studies and martial arts breakthrough', '<p><span style="color: rgb(32, 33, 34);">Until his late teens, Lee''s street fights became more frequent and included beating the son of a feared&nbsp;</span><a href="https://en.wikipedia.org/wiki/Triad_(organized_crime)" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c);">triad</a><span style="color: rgb(32, 33, 34);">&nbsp;family.</span><a href="https://en.wikipedia.org/wiki/Bruce_Lee#cite_note-LeeLee1989-42" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c); background-color: initial;"><sup>[39]</sup></a><span style="color: rgb(32, 33, 34);">&nbsp;In 1958, after students from a rival&nbsp;</span><a href="https://en.wikipedia.org/wiki/Choy_Li_Fut" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c);">Choy Li Fut</a><span style="color: rgb(32, 33, 34);">&nbsp;martial arts school challenged Lee''s&nbsp;</span><a href="https://en.wikipedia.org/wiki/Wing_Chun" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c);">Wing Chun</a><span style="color: rgb(32, 33, 34);">&nbsp;school, he engaged in a fight on a rooftop. In response to an unfair punch by another boy, he beat him so badly that he knocked out one of his teeth, leading to a complaint by the boy''s parents to the police.</span><a href="https://en.wikipedia.org/wiki/Bruce_Lee#cite_note-Burrows_2002-43" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c); background-color: initial;"><sup>[40]</sup></a></p>', 'Now', '1', 'Tue Jun 04 2024 10:55:56 GMT+0200 (Central European Summer Time)', 'null', 'Public', 'Person of Interest', 'admin', 'Tue Jun 04 2024 09:53:18 GMT+0200 (Central European Summer Time)', 'Tue Jun 04 2024 23:56:11 GMT+0200 (Central European Summer Time)', '23');
INSERT INTO works (id, person_id, title, content, publishTime, isPublished, scheduledPublishTime, externalSource, visibility, category, createdBy, created_at, updated_at, work_view_count) VALUES ('235', '159', '1966–1970: American roles and creating Jeet Kune Do', '<p><span style="color: rgb(32, 33, 34);">From 1966 to 1967, Lee played the role of&nbsp;</span><a href="https://en.wikipedia.org/wiki/Kato_(The_Green_Hornet)" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c);">Kato</a><span style="color: rgb(32, 33, 34);">&nbsp;alongside the title character played by&nbsp;</span><a href="https://en.wikipedia.org/wiki/Van_Williams" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c);">Van Williams</a><span style="color: rgb(32, 33, 34);">&nbsp;in the TV series produced and narrated by&nbsp;</span><a href="https://en.wikipedia.org/wiki/William_Dozier" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c);">William Dozier</a><a href="https://en.wikipedia.org/wiki/Bruce_Lee#cite_note-60" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c); background-color: initial;"><sup>[57]</sup></a><span style="color: rgb(32, 33, 34);">&nbsp;titled&nbsp;</span><a href="https://en.wikipedia.org/wiki/The_Green_Hornet_(TV_series)" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c); background-color: initial;"><em>The Green Hornet</em></a><span style="color: rgb(32, 33, 34);">, based on the radio show by the same name.</span><a href="https://en.wikipedia.org/wiki/Bruce_Lee#cite_note-61" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c); background-color: initial;"><sup>[58]</sup></a><a href="https://en.wikipedia.org/wiki/Bruce_Lee#cite_note-:0-59" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c); background-color: initial;"><sup>[56]</sup></a><span style="color: rgb(32, 33, 34);">&nbsp;The show ran for one season (26 episodes) from September 1966 to March 1967. Lee and Williams also appeared as their characters in three&nbsp;</span><a href="https://en.wikipedia.org/wiki/Crossover_(fiction)" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c);">crossover</a><span style="color: rgb(32, 33, 34);">&nbsp;episodes of&nbsp;</span><a href="https://en.wikipedia.org/wiki/Batman_(TV_series)" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c); background-color: initial;"><em>Batman</em></a><span style="color: rgb(32, 33, 34);">, another William Dozier-produced television series.</span><a href="https://en.wikipedia.org/wiki/Bruce_Lee#cite_note-62" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c); background-color: initial;"><sup>[59]</sup></a><a href="https://en.wikipedia.org/wiki/Bruce_Lee#cite_note-63" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c); background-color: initial;"><sup>[60]</sup></a><a href="https://en.wikipedia.org/wiki/Bruce_Lee#cite_note-64" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c); background-color: initial;"><sup>[61]</sup></a></p>', 'Now', '1', 'Tue Jun 04 2024 08:30:02 GMT+0200 (Central European Summer Time)', 'null', 'Public', 'null', 'admin', 'Tue Jun 04 2024 10:30:44 GMT+0200 (Central European Summer Time)', 'Tue Jun 04 2024 17:31:34 GMT+0200 (Central European Summer Time)', '9');
INSERT INTO works (id, person_id, title, content, publishTime, isPublished, scheduledPublishTime, externalSource, visibility, category, createdBy, created_at, updated_at, work_view_count) VALUES ('239', '159', '1971–1973: Hong Kong films and Hollywood breakthrough', '<p><span style="color: rgb(32, 33, 34);">In 1971, Lee appeared in four episodes of the television series&nbsp;</span><a href="https://en.wikipedia.org/wiki/Longstreet_(TV_series)" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c); background-color: initial;"><em>Longstreet</em></a><span style="color: rgb(32, 33, 34);">, written by Silliphant. Lee played Li Tsung, the martial arts instructor of the title character Mike Longstreet, played by&nbsp;</span><a href="https://en.wikipedia.org/wiki/James_Franciscus" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c);">James Franciscus</a><span style="color: rgb(32, 33, 34);">, and important aspects of his martial arts philosophy were written into the script.</span><a href="https://en.wikipedia.org/wiki/Bruce_Lee#cite_note-:1-78" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c); background-color: initial;"><sup>[75]</sup></a><a href="https://en.wikipedia.org/wiki/Bruce_Lee#cite_note-:6-79" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c); background-color: initial;"><sup>[76]</sup></a><span style="color: rgb(32, 33, 34);">&nbsp;According to statements made by Lee, and also by&nbsp;</span><a href="https://en.wikipedia.org/wiki/Linda_Lee_Cadwell" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c);">Linda Lee Cadwell</a><span style="color: rgb(32, 33, 34);">&nbsp;after Lee''s death, Lee pitched a television series of his own in 1971, tentatively titled&nbsp;</span><a href="https://en.wikipedia.org/wiki/Warrior_(TV_series)" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c);"><em>The Warrior</em></a><span style="color: rgb(32, 33, 34);">, discussions of which were confirmed by&nbsp;</span><a href="https://en.wikipedia.org/wiki/Warner_Bros." rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c);">Warner Bros.</a><span style="color: rgb(32, 33, 34);">&nbsp;During a December 9, 1971, television interview on&nbsp;</span><a href="https://en.wikipedia.org/wiki/The_Pierre_Berton_Show" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c); background-color: initial;"><em>The Pierre Berton Show</em></a><span style="color: rgb(32, 33, 34);">, Lee stated that both&nbsp;</span><a href="https://en.wikipedia.org/wiki/Paramount_Pictures" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c);">Paramount</a><span style="color: rgb(32, 33, 34);">&nbsp;and Warner Brothers wanted him "to be in a modernized type of a thing and that they think the Western idea is out, whereas I want to do the Western".</span><a href="https://en.wikipedia.org/wiki/Bruce_Lee#cite_note-80" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c); background-color: initial;"><sup>[77]</sup></a></p>', 'Now', '1', 'Tue Jun 04 2024 08:37:45 GMT+0200 (Central European Summer Time)', 'null', 'Public', 'null', 'admin', 'Tue Jun 04 2024 10:38:07 GMT+0200 (Central European Summer Time)', 'Tue Jun 04 2024 17:59:09 GMT+0200 (Central European Summer Time)', '15');
