INSERT INTO works (id, person_id, title, content, publishTime, isPublished, scheduledPublishTime, externalSource, visibility, category, createdBy, created_at, updated_at, work_view_count) VALUES ('241', '160', 'A krv mi vri', '<p><span style="color: rgb(32, 33, 34);">Irina Kapetanović-Perduv je rođena 20. marta 1981. godine u&nbsp;</span><a href="https://bs.wikipedia.org/wiki/Konjic" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c);">Konjicu</a><span style="color: rgb(32, 33, 34);">. Muzičku karijeru je započela sa grupom&nbsp;</span><em style="color: rgb(32, 33, 34);">Drugo stanje</em><span style="color: rgb(32, 33, 34);">, sa kojima je izdala dva albuma: "Tragač" i "Nemir". kasnije napušta grupu, odlazi i rodnog Konjica u Sarajevo i osniva grupu&nbsp;</span><em style="color: rgb(32, 33, 34);">Irina &amp; VI</em><span style="color: rgb(32, 33, 34);">&nbsp;2002. godine. Godine 2004. dobija nagradu&nbsp;</span><em style="color: rgb(32, 33, 34);">Davorin</em><span style="color: rgb(32, 33, 34);">&nbsp;za najbolju žensku vokalnu izvedbu. Među njenim najvećim hitovima ističu se: "Mrazevi", "Sad se briše i blijedi", "Gavran", "Tek što je svanulo" i drugi. Nakon nekog vremena, grupa se raspala, a Irina se povukla iz javnosti.</span><a href="https://bs.wikipedia.org/wiki/Irina_%26_VI#cite_note-1" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c); background-color: initial;"><sup>[1]</sup></a><span style="color: rgb(32, 33, 34);">&nbsp;Irina danas živi u Konjicu sa suprugom Todorom Perduvom iz Vukovara, za kojeg se udala u septembru 2012. godine.</span><a href="https://bs.wikipedia.org/wiki/Irina_%26_VI#cite_note-2" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c); background-color: initial;"><sup>[2]</sup></a><span style="color: rgb(32, 33, 34);">&nbsp;Poslije duže pauze, najavila je izdavanje solo albuma.</span><a href="https://bs.wikipedia.org/wiki/Irina_%26_VI#cite_note-3" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c); background-color: initial;"><sup>[3]</sup></a></p>', 'Now', '1', 'Tue Jun 04 2024 17:26:50 GMT+0200 (Central European Summer Time)', 'null', 'Public', 'Person of Interest', 'admin', 'Tue Jun 04 2024 11:31:36 GMT+0200 (Central European Summer Time)', 'Tue Jun 04 2024 18:14:24 GMT+0200 (Central European Summer Time)', '7');
