INSERT INTO works (id, person_id, title, content, publishTime, isPublished, scheduledPublishTime, externalSource, visibility, category, createdBy, created_at, updated_at, work_view_count) VALUES ('243', '162', 'Chicago Bulls (1984–1993; 1995–1998)', '<p><span style="color: rgb(32, 33, 34);">The&nbsp;</span><a href="https://en.wikipedia.org/wiki/1984%E2%80%9385_Chicago_Bulls_season" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c);">Chicago Bulls</a><span style="color: rgb(32, 33, 34);">&nbsp;selected Jordan with the third overall pick of the&nbsp;</span><a href="https://en.wikipedia.org/wiki/1984_NBA_draft" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c);">1984 NBA draft</a><span style="color: rgb(32, 33, 34);">&nbsp;after&nbsp;</span><a href="https://en.wikipedia.org/wiki/Hakeem_Olajuwon" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c);">Hakeem Olajuwon</a><span style="color: rgb(32, 33, 34);">&nbsp;(</span><a href="https://en.wikipedia.org/wiki/1984%E2%80%9385_Houston_Rockets_season" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c);">Houston Rockets</a><span style="color: rgb(32, 33, 34);">) and&nbsp;</span><a href="https://en.wikipedia.org/wiki/Sam_Bowie" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c);">Sam Bowie</a><span style="color: rgb(32, 33, 34);">&nbsp;(</span><a href="https://en.wikipedia.org/wiki/1984%E2%80%9385_Portland_Trail_Blazers_season" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c);">Portland Trail Blazers</a><span style="color: rgb(32, 33, 34);">). One of the primary reasons why Jordan was not drafted sooner was because the first two teams were in need of a&nbsp;</span><a href="https://en.wikipedia.org/wiki/Center_(basketball)" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c);">center</a><span style="color: rgb(32, 33, 34);">.</span><a href="https://en.wikipedia.org/wiki/Michael_Jordan#cite_note-60" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c); background-color: initial;"><sup>[58]</sup></a><span style="color: rgb(32, 33, 34);">&nbsp;Trail Blazers general manager&nbsp;</span><a href="https://en.wikipedia.org/wiki/Stu_Inman" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c);">Stu Inman</a><span style="color: rgb(32, 33, 34);">&nbsp;contended that it was not a matter of drafting a center but more a matter of taking Bowie over Jordan, in part because Portland already had&nbsp;</span><a href="https://en.wikipedia.org/wiki/Clyde_Drexler" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c);">Clyde Drexler</a><span style="color: rgb(32, 33, 34);">, who was a guard with similar skills to Jordan.</span><a href="https://en.wikipedia.org/wiki/Michael_Jordan#cite_note-61" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c); background-color: initial;"><sup>[59]</sup></a><span style="color: rgb(32, 33, 34);">&nbsp;Citing Bowie''s injury-laden college career,&nbsp;</span><a href="https://en.wikipedia.org/wiki/ESPN" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c);">ESPN</a><span style="color: rgb(32, 33, 34);">&nbsp;named the Blazers'' choice of Bowie as the worst draft pick in North American professional sports history.</span><a href="https://en.wikipedia.org/wiki/Michael_Jordan#cite_note-62" rel="noopener noreferrer" target="_blank" style="color: var(--color-progressive,#36c); background-color: initial;"><sup>[60]</sup></a></p>', 'Now', '1', 'Tue Jun 04 2024 16:48:39 GMT+0200 (Central European Summer Time)', 'null', 'Public', 'null', 'admin', 'Tue Jun 04 2024 18:48:57 GMT+0200 (Central European Summer Time)', 'Tue Jun 04 2024 20:48:25 GMT+0200 (Central European Summer Time)', '2');
INSERT INTO works (id, person_id, title, content, publishTime, isPublished, scheduledPublishTime, externalSource, visibility, category, createdBy, created_at, updated_at, work_view_count) VALUES ('244', '161', '1977–1983: Early impressionist work in Toronto', '<p>Carrey''s first stand-up comedy experience took place in 1977 at the age of 15 with his father trying to help him put together a stage act, driving him to downtown Toronto to debut at the recently-opened Yuk Yuk''s comedy club operating one-night-a-week out of community centre The 519''s basement on Church Street.[17][19] For the performance, Carrey had his attire—a polyester leisure suit—chosen by his mother who reasoned "that''s how they dress on The Dean Martin Celebrity Roast".[20][17] Pubescent Carrey''s conventional impersonations bombed, proving ill-suited for a club with a raunchy comedic sensibility and giving him doubts about his potential as a professional entertainer.[17] Decades later, recalling Carrey''s stand-up debut, Yuk Yuk''s owner Mark Breslin described it as "bad Rich Little".[17] His family''s financial struggles made it difficult for them to support Carrey''s show business ambitions.</p>', 'Now', '1', 'Tue Jun 04 2024 16:50:43 GMT+0200 (Central European Summer Time)', 'null', 'Public', 'null', 'admin', 'Tue Jun 04 2024 18:51:21 GMT+0200 (Central European Summer Time)', 'Tue Jun 04 2024 18:51:21 GMT+0200 (Central European Summer Time)', '0');
